import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import '../models/chat_user.dart';//card to represent a single user in home screenclass ChatUserCard extends StatefulWidget {  final ChatUser user;  const ChatUserCard({Key? key, required this.user}) : super(key: key);  @override  State<ChatUserCard> createState() => _ChatUserCardState();}class _ChatUserCardState extends State<ChatUserCard> {  @override  Widget build(BuildContext context) {    return Card(      margin: EdgeInsets.symmetric(horizontal: 15,      vertical: 5,),      borderOnForeground: true,      //color: Colors.grey[300],      elevation: 0.5,      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(15)      ),      child: InkWell(        onTap: (){},        child: ListTile(          //user profile picture          leading: CircleAvatar(            child: Icon(CupertinoIcons.person),          ),          //user name          title: Text(widget.user.name,          style: TextStyle(            fontWeight: FontWeight.bold          ),),          //last message          subtitle: Text(widget.user.about,          style: TextStyle(            fontSize: 13          ),            //last message time          maxLines: 1,),          trailing: Text('12:00 PM',style:            TextStyle(              color: Colors.black54,              fontWeight: FontWeight.bold,                fontSize: 13            ),),        ),      ),    );  }}