import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import '../models/chat_user.dart';class ChatUserCard extends StatefulWidget {  final ChatUser user;  const ChatUserCard({Key? key, required this.user}) : super(key: key);  @override  State<ChatUserCard> createState() => _ChatUserCardState();}class _ChatUserCardState extends State<ChatUserCard> {  @override  Widget build(BuildContext context) {    return Card(      margin: EdgeInsets.symmetric(horizontal: 15,      vertical: 5,),      //color: Colors.blue.shade100,      elevation: 0.5,      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(15)      ),      child: InkWell(        onTap: (){},        child: ListTile(          leading: CircleAvatar(            child: Icon(CupertinoIcons.person),          ),          title: Text(widget.user.name,          style: TextStyle(            fontWeight: FontWeight.bold          ),),          subtitle: Text(widget.user.about,          style: TextStyle(            fontSize: 13          ),          maxLines: 1,),          trailing: Text('12:00 PM',style:            TextStyle(              color: Colors.black54,              fontWeight: FontWeight.bold,                fontSize: 13            ),),        ),      ),    );  }}