import 'package:cloud_firestore/cloud_firestore.dart';import 'package:firebase_auth/firebase_auth.dart';import '../models/chat_user.dart';class API {  // for authentication  static FirebaseAuth auth = FirebaseAuth.instance;  // for accessing cloud firestore database  static FirebaseFirestore fireStore = FirebaseFirestore.instance;  // for checking if user exists or not?  static Future<bool> userExist () async  {    return (await fireStore.collection      ('users').doc(auth.currentUser!.uid).get()).exists;  }  // to return current user  static User get user => auth.currentUser!;  // for creating a new user  static Future<void> createUser () async  {    final time = DateTime.now().toString();    final chatUser = ChatUser(      id: user.uid,      name: user.displayName.toString(),      email: user.email.toString(),      about: "Hey I'm Using We Chat",      image: user.photoURL.toString(),      createdAt: time,      isOnline: false,      lastActive: time,      pushToken: '' ,    );    return await fireStore.collection      ('users').doc(user.uid).set(chatUser.toJson());  }}